import { all, call, put, fork, delay, takeEvery } from 'redux-saga/effects';
import axios from 'axios';

import { 
    LOG_IN_SUCCESS, 
    LOG_IN_FAILURE,
    LOG_IN_REQUEST,
    SIGN_UP_FAILURE,
    SIGN_UP_REQUEST,
    SIGN_UP_SUCCESS 
} from '../reducers/user';

// login pattern

function loginAPI() {
    // 서버에 요청을 보내는 부분
    console.log('server login ~');
    return axios.post('/login');
}

function* login() {
    try {
        yield call(loginAPI);
        yield delay(2000);
        yield put({ //디스패치와 동일
            type: LOG_IN_SUCCESS
        });
    } catch (e) {
        console.error(e);
        yield put({
            type: LOG_IN_FAILURE
        });
    }
}

function* watchLogin() {
    yield takeEvery(LOG_IN_REQUEST, login);
}

// signUp pattern

function signUpAPI() {
    console.log('signUpAPI');
    return axios.post('/signup');
}

function* signUp() {
    try {
      yield call(signUpAPI);
      yield delay(2000);
      // throw new Error('에러에러에러');
      yield put({ // put은 dispatch 동일
        type: SIGN_UP_SUCCESS,
      });
    } catch (e) { // loginAPI 실패
      console.error(e);
      yield put({
        type: SIGN_UP_FAILURE,
        error: e,
      });
    }
}

function* watchSignUp() {
    yield takeEvery(SIGN_UP_REQUEST, signUp);
}

export default function* userSagar() {
    yield all([
        fork(watchLogin),
        fork(watchSignUp),
    ]);
}
