import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu, Input, Button, Row, Col, Card, Avatar } from 'antd';

const dummy = {
    nickname: "jaden",
    Postings: [],
    Followings: [1, 2, 3],
    Followers: [1, 2],
};

const AppLayout = ({ children }) => {
    console.log('app layout rendered');
    return (
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>Node Bird</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>Profile</a></Link></Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={{ verticalAlign: 'middle' }}></Input.Search>
                </Menu.Item>
            </Menu>

            <Row>
                <Col xs={24} md={6}>
                    <Card
                        actions={[
                            <div key="posting">posting<br />{dummy.Postings.length}</div>,
                            <div key="following">following<br />{dummy.Followings.length}</div>,
                            <div key="follower">follower<br />{dummy.Followers.length}</div>
                        ]}
                    >
                    <Card.Meta
                        avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                        title={dummy.nickname}
                    />
                    </Card>
                    <Form>
                        <div>
                            <label htmlFor="user-id">ID</label>
                            <br />
                            <Input name="user-id" value={userId} onChange={onChangeId} required />
                        </div>
                        <div>
                            <label htmlFor="user-password">Password</label>
                            <br />
                            <Input name="user-password" value={userId} onChange={onChangeId} required />
                        </div>
                    </Form>
                    <Button><Link href="/signup"><a>Sign up</a></Link></Button>
                </Col>
                <Col xs={24} md={12}>
                    {children}
                </Col>
                <Col xs={24} md={6}>
                    3
                </Col>
            </Row>

        </div>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node,
};

export default AppLayout;
