        yield put({ // put은 dispatch 동일
            type: LOG_OUT_SUCCESS,
        });
    } catch (e) { // logout 실패
        console.log('!!!!');
        console.error(e);
        yield put({
            type: LOG_OUT_SUCCESS,
            error: e,
        });
